<?php
//https://siliconvalleyvigo.com/get-image-url/example.php
// URL del archivo PHP
$url = "https://siliconvalleyvigo.com/apis-genericas/get-image-url/";

// Definimos la clave de permiso
$clave_permiso = getenv('GET_IMAGE_KEY');

// Datos a enviar en la petici贸n POST
$data = array(
  "key" => $clave_permiso,
  "file" => "",
  "type" => "jpg"
);

// Inicializamos cURL
$curl = curl_init();

// Configuramos las opciones de la petici贸n cURL
curl_setopt($curl, CURLOPT_URL, $url);
curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
curl_setopt($curl, CURLOPT_POST, true);
curl_setopt($curl, CURLOPT_POSTFIELDS, http_build_query($data));

// Ejecutamos la petici贸n cURL y obtenemos la respuesta
$response = curl_exec($curl);

// Cerramos la sesi贸n cURL
curl_close($curl);

// Mostramos la respuesta obtenida
echo $response;